import React from 'react';
import './Home.css';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();

  const handleQuickSearch = (word) => {
    navigate(`/dictionary?word=${word}`);
  };

  return (
    <div className="home-container">
      <h1>ðŸ‘‹ Welcome to the Dictionary App</h1>
      <p className="tagline">Instant definitions, synonyms, and pronunciation help.</p>

      <div className="quick-search">
        <h3>Try one of these:</h3>
        <div className="word-buttons">
          {['react', 'hello', 'peace', 'education', 'ocean'].map((w) => (
            <button key={w} onClick={() => handleQuickSearch(w)}>
              {w}
            </button>
          ))}
        </div>
      </div>

      <div className="about">
        <h3>ðŸ“š About this app</h3>
        <p>
          This app uses the <strong>Free Dictionary API</strong> to fetch real-time word definitions, parts of speech, pronunciation, and synonyms. Click on the "Dictionary" tab to get started!
        </p>
      </div>
    </div>
  );
};

export default Home;
