import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css'; // üëà custom CSS file

const Register = () => {
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();

    if (newUsername && newPassword) {
      // Save credentials to localStorage
      localStorage.setItem(
        'user',
        JSON.stringify({ username: newUsername, password: newPassword })
      );
      setMessage('‚úÖ Registered! Redirecting to login...');

      // Clear fields
      setNewUsername('');
      setNewPassword('');

      // Redirect to login after delay
      setTimeout(() => {
        navigate('/login');
      }, 1500);
    } else {
      setMessage('‚ùå Please fill in all fields');
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleRegister}>
        <h2>Register</h2>
        <input
          type="text"
          placeholder="Enter Username"
          value={newUsername}
          onChange={(e) => setNewUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button type="submit">Register</button>
        {message && <p className="register-msg">{message}</p>}
      </form>
    </div>
  );
};

export default Register;
